import consumer from "./consumer"

consumer.subscriptions.create("NotificationsChannel", {
  connected() {
    // Called when the subscription is ready for use on the server
    console.log('ok')
  },

  disconnected() {
    // Called when the subscription has been terminated by the server
  },

  received(data) {
    console.log(data)

    // $('.badge+' + data.received_id).html("")
    // $('.badge+' + data.received_id).append(data.count)
    $('.notifications').find("#icon-badge+"+data.received_id).html("")
    $('.notifications').find("#icon-badge+"+data.received_id).append(data.count)
    $('.badge+133').append(data.count)
    // Called when there's incoming data on the websocket for this channel
    // Called when there's incoming data on the websocket for this channel
  }
});
